// MD5º”√‹public static String md5(String s) {if (s != null) {String md5_str = "";try {MessageDigest m = MessageDigest.getInstance("MD5");m.update(s.getBytes(), 0, s.length());md5_str = new BigInteger(1, m.digest()).toString(16);// Log.i("MD5", md5_str);if (md5_str.length() < 32) {md5_str = "0".concat(md5_str);}} catch (NoSuchAlgorithmException e) {e.printStackTrace();}return md5_str;}return "";}